mapper.update:
update Member
set password = #{password}
where name = #{name}

mapper.insert:
INSERT INTO
    Member (
        id
      , password
      , type
      , email
      , username
      , status
      , created_at
      )
VALUES (
    #{id}
  , #{password}
  , #{type}
  , #{email}
  , #{username}
  , 'active'
  , NOW()
);

INSERT INTO member_activity_log(member_seq, login_time, ip_address,activity_type)
VALUES (
            #{memberSeq}
          , NOW()
          , #{ipAddress}
          , #{activityType}
       );
