mapper.insert:
INSERT INTO joo.news_letter ( /*insertNewsLetter*/
    news_letter_header_seq
  , title
  , description
  , content
  , cost
  , free_yn
  , view
  , reg_date
    )
VALUES (
    #{param.newsLetterHeaderSeq}
  , #{param.title}
  , #{param.description}
  , #{param.content}
  , #{param.cost}
  , #{param.freeYn}
  , 0
  , NOW()
)

INSERT INTO  /*insertNewsLetterHeader*/
    joo.news_letter_header (
        header_title
      , category_combi_seq
      , week_day
      , author_id
      , reg_date
      , db_status
    )
VALUES (
    #{param.headerTitle}
  , #{param.categoryCombiSeq}
  , #{param.weekDay}
  , #{param.authorId}
  , NOW()
  , 'OK'
    )

mapper.update:
UPDATE  joo.news_letter
SET
    title = #{param.title}
  , description = #{param.description}
  , content = #{param.content}
  , cost = #{param.cost}
  , freeYn = #{param.freeYn}
  , upd_date = NOW()
WHERE news_letter_seq = #{param.newsLetterSeq}

mapper.insert:
INSERT INTO  /*insertImagePath*/
    joo.image_path (
        news_letter_seq
      , news_letter_header_seq
      , image_path
      , origin_name
      , saved_name
      , reg_date
)
VALUES (
           #{param.newsLetterSeq}
       , #{param.newsLetterHeaderSeq}
       , #{param.imagePath}
       , #{param.originName}
       , #{param.savedName}
       , NOW()
       )
